import useInput from "../../hooks/use-input"
const CartForm = props => {
    const {
        enteredValue: enteredName,
        hasError: hasNameError,
        setInputValue: handleNameChange,
        handleBlur: handleNameBlur,
        reset: nameReset
    } = useInput(value => value.trim() !== '');

    const {
        enteredValue: enteredLastName,
        hasError: hasLastNameError,
        setInputValue: handleLastNameChange,
        handleBlur: handleLastNameBlur,
        reset: LastNameReset
    } = useInput(value => value.trim() !== '');

    const {
        enteredValue: enteredEmail,
        hasError: hasEmailError,
        setInputValue: handleEmailChange,
        handleBlur: handleEmailBlur,
        reset: EmailReset
    } = useInput(value => value.includes('@'));




    const formSubmitHandler = (e) => {
        e.preventDefault();
        nameReset()

    }
    return (
        <form onSubmit={formSubmitHandler}>
            <div>
                <input type="text" name="name" placeholder="Twoje imię" value={enteredName} onChange={handleNameChange} onBlur={handleNameBlur} />
                {hasNameError && <p style={{ color: 'red' }}>Name must not be empty</p>}
            </div>
            <div>
                <input type="text" name="name" placeholder="Twoje nazwisko" value={enteredLastName} onChange={handleLastNameChange} onBlur={handleLastNameBlur} />
                {hasLastNameError && <p style={{ color: 'red' }}>Last Name must not be empty</p>}
            </div>
            <div>
                <input type="text" name="email" placeholder="Twój email" value={enteredEmail} onChange={handleEmailChange} onBlur={handleEmailBlur} />
                {hasEmailError && <p style={{ color: 'red' }}>Email must contain @</p>}
            </div>

        </form>)

}

export default CartForm
